import React from 'react';
import AppLayout from '../components/AppLayout';
import {Form, Input, Checkbox, Button} from 'antd';

const Signup = () => {
    const [id, setId] = useState('');
    const [nick, setNick] = useState('');
    const [password, setPassword] = useState('');
    const [passwordCheck, setPasswordCheck] = useState('');

    const onSubmit = () => {};
    const onChangeId = () => {};
    const onChangeNick = () => {};
    const onChangePassword = () => {};
    const onChangePasswordChk = () => {};
    const onChangeTerm = () => {};

    return (
        <>
            <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/3.25.3/antd.css" />

            <AppLayout>
                <Form onSubmit={onSubmit} style={{padding: 10}}>
                    <div>
                        <label htmlFor="user-id">아이디</label>
                        <br />
                        <Input name="user-id" required onChange={onChangeId}></Input>
                    </div>
                    <div>
                        <label htmlFor="user-nick">닉네임</label>
                        <br />
                        <Input name="user-nick" required onChange={onChangeNick}></Input>
                    </div>
                    <div>
                        <label htmlFor="user-pass">비밀번호</label>
                        <br />
                        <Input name="user-pass" type="password" required onChange={onChangePassword}></Input>
                    </div>
                    <div>
                        <label htmlFor="user-pass-chk">비밀번호체크</label>
                        <br />
                        <Input name="user-pass-chk" type="password" required onChange={onChangePasswordChk}></Input>
                    </div>
                    <div>
                        <Checkbox name="user-term" onChange={onChangeTerm}>제로초 말을 잘 들을 것을 동의합니다.</Checkbox>
                    </div>
                    <div>
                        <Button type="primary" htmlType="submit">가입하기</Button>
                    </div>
                </Form>
            </AppLayout>
        </>
    );
};

export default Signup;
